import { useParams } from "react-router-dom"
import axios from "axios";
import { useState,useEffect } from "react";
import { useDispatch,useSelector } from "react-redux";
import { addMovie } from "../Redux/action";
import { useNavigate } from "react-router-dom";
 export const Payment=()=>{
    let navigate = useNavigate();
    const {id} = useParams();
    const [movie, setMovie] = useState({})
    const [noseats,noseatselect]=useState(1)
    let mk=((Math.floor(Math.random() * 500) + 120)*noseats)
   let  lk=Math.floor(Math.random() * 100) + 20
   const [seats,seatselect]=useState(true)

    useEffect(() => {
		getMovies();

},[])
const getMovies = () => {
	axios.get(`https://bookmyshow-clone-fw14.herokuapp.com/movies/${id}`).then((res) => {
		setMovie(res.data)


	})

}
const takeseat=(bv)=>{
    noseatselect(bv)
}
const payit=()=>{
    seatselect(!seats)
}
const product=useSelector((store)=>

store.product.product
)

const bookmovie=()=>{

        alert("Paymet Sucessfull")
        navigate('/')


}
    return(

        <div>
            {seats ? <div className="ticketdiv">
            <div className="ticketpop">
                <div className="kjvfsfli">Select Seat</div>
                <span><span onClick={()=>{
                    takeseat(1)
                }}>1</span><span onClick={()=>{
                    takeseat(2)
                }}>2</span><span onClick={()=>{
                    takeseat(3)
                }}>3</span><span onClick={()=>{
                    takeseat(4)
                }}>4</span><span onClick={()=>{
                    takeseat(5)
                }}>5</span><span onClick={()=>{
                    takeseat(6)
                }}>6</span><span onClick={()=>{
                    takeseat(7)
                }}>7</span><span onClick={()=>{
                    takeseat(8)
                }}>8</span><span onClick={()=>{
                    takeseat(9)
                }}>9</span><span onClick={()=>{
                    takeseat(10)
                }}>10</span></span>
                <div className="kjvfsfla">No seat : {noseats}</div>
                <div onClick={payit} className="kjvfsfln">Pay</div>
            </div>
        </div>:""}

